void cb_InsertTag(Fl_Widget*, void* Tag) {
  Fl_New_File* st=(Fl_New_File *)(txt_Tab_Edit->value());  
  if (!(txt_Tab_Edit->visible())) // check if the text buffer is visible
        return;
  st->e->insert_mode(1);// inset not replace
  char *selectedText=st->t->selection_text();
  if (strlen(selectedText)==0)
   {
     st->t->insert(st->e->GetCursorPosition(),(char *)Tag);
     int coun=strlen((char*)Tag);
     char *seekP=(char*)Tag;
    while(!(seekP[coun-1]=='<' && seekP[coun]=='/')  && coun>0)
    {
      coun--;
    }
     coun--;
     st->e->insert_position(st->e->insert_position()+coun);
     st->e->focus(st->e);
   }
  else
  {
     st->t->remove_selection(); 
     st->t->insert(st->e->GetCursorPosition(),(char *)Tag);
     int coun=strlen((char*)Tag);
     char *seekP=(char*)Tag;
     while(!(seekP[coun-1]=='<' && seekP[coun]=='/')  && coun>0)
    {
      coun--;
    }
     coun--;
     st->t->insert(st->e->insert_position()+coun, selectedText);
     st->e->insert_position(st->e->insert_position()+strlen((char*)Tag)+strlen(selectedText));
     st->e->focus(st->e);  
  }
  free(selectedText); // Free the memory
}

