void cb_File_New() {
  // New Tab widget whould be made when you click New button.
  Fl_New_File *Prev =(Fl_New_File*)txt_Tab_Edit->value();  // get active tab
  if(Prev)
  {
    while (Prev->pNext)
      Prev=Prev->pNext; // get the last one 
  }
  
  txt_Tab_Edit->begin();
  char buf[30];
  snprintf(buf, sizeof(Fl_New_File::counter)+10 ,"Untitiled %d",Fl_New_File::counter);
  Fl_New_File *st=new Fl_New_File(230,125,1050,840,"");
  
  st->pPrev=Prev; // make the link
  if(Prev)
    Prev->pNext=st; // make the link
  
  st->NotSaved=1; // Not saved
  st->copy_label(buf);
  strcpy(st->fileName,buf);
  Fl_New_File::counter++;
  txt_Tab_Edit->end();
  txt_Tab_Edit->value(st);
  txt_Tab_Edit->redraw();
  return;
}

int check_save(void) {
  // check if it need to be saved
  
  Fl_New_File* activeTab=(Fl_New_File*)txt_Tab_Edit->value();
     if ((!activeTab->changed)&&(!activeTab->NotSaved))
           return 1;
    int r = fl_choice("The current file has not been saved.\n"
                      "Would you like to save it now?",
                      "Cancel", "Save", "Don't Save");
    if (r==0) 
        return -1; // do nothing  cancel 
    if (r == 1)
   {
      cb_File_Save(activeTab->fileName); // Save the file...
      return !activeTab->changed;
   }
    return (r == 2) ? 1 : 0;
}

void cb_File_Close() {
  //Close file ... Before that we should check if it is saved
  Fl_New_File* activeTab=(Fl_New_File*)txt_Tab_Edit->value();
  int CheckSave=check_save();
      if (CheckSave==0)
    {
  	cb_File_Save(activeTab->fileName);
    }
    if (CheckSave==-1)
       return; // don't close the file .. 
       
       activeTab->hide();
  //     activeTab->t->remove_modify_callback(changed_cb,activeTab);
       if (!(activeTab->pNext)) // if there is next give focus to it or to the previous.
           txt_Tab_Edit->value(activeTab->pPrev);
         else
           txt_Tab_Edit->value(activeTab->pNext);
        if(activeTab)
       {
           if (activeTab->pNext==0) // it was the last item
             {  if(activeTab->pPrev)
                 ((activeTab->pPrev)->pNext)=0;
             }
           else
             if (activeTab->pPrev) 
                 activeTab->pPrev->pNext=activeTab->pNext;// connect the prev to the next and neglect this 
  
          activeTab->clear();
          txt_Tab_Edit->remove(activeTab);
          delete (activeTab);
          Fl_New_File::counter--;
      }
  
       txt_Tab_Edit->damage();
    if (Fl_New_File::counter == 0)
       cb_File_New();
  return;
}

void cb_File_Save(char* newfile) {
  Fl_New_File* st=(Fl_New_File *)txt_Tab_Edit->value();     
  if ((st->NotSaved))  // needs save as
  {
      cb_File_SaveAs();
  }
  else
  {
    if (st->t->savefile(st->fileName)) // this will save the file
      fl_alert("Error writing to file \'%s\':\n%s.", newfile, strerror(errno));
    else
    st->changed = 0;
    st->NotSaved=0;
    st->t->call_modify_callbacks();
  }
}

