char* InsertTagWithAText(const char* TagOrigional, const char* GetSymbol, const char* TextInTag) {
  //Insert Tag After A char
   const char* pToTagOrigional=TagOrigional;
   int lengthOfTheTag=strlen(TagOrigional)+1;
   char *buf=new char[lengthOfTheTag+strlen(TextInTag)+1];
    for (int i=1;i<lengthOfTheTag;i++)
    {
       if(*(pToTagOrigional)==*GetSymbol)
       {
       pToTagOrigional++;
       i++;
        strncpy (buf,TagOrigional,i);
        strcat(buf,TextInTag);
        strcat(buf,pToTagOrigional);
       return buf;
       }
      pToTagOrigional++;
       
    }
   return "";
}

