void cb_FontBrowser(Fl_Widget*,  void* frm_FontBrowser) {
  // Get the font browsers value and insert it to the Text Editor (Active tab) or Activate the tab
  
  Fl_Font_Browser *fb=(Fl_Font_Browser*)frm_FontBrowser;
  
  char buf[255]; // Max font selection length estimated as 255!
  char buf2[255];
  
  
  // font face = fb->lst_Font->text(fb->lst_Font->value());
  //font size = fb->pickedsize;
  //font color = fb->box_Example->GetFontColor();
  sprintf (buf,"%s%d\"  %s %s\"  %s %X  %s ", "<font  size=\"",fb->pickedsize ," face=\"", fb->lst_Font->text(fb->lst_Font->value()), "color=\"", fb->box_Example->GetFontColor(), "\">  </font>");
  
  // font style=  fb->GetFontStyle()
  
  
  if (fb->box_Example->GetFontStyle()>0)
    switch (fb->box_Example->GetFontStyle())
     {
       case 1: // Bold selected 
             {
               sprintf(buf2,"%s",InsertTagWithAText("<b>  </b>",">",buf));
               sprintf(buf,"%s",buf2);
              break;
                        
             
             }
    
       case 2: 
             {
             
              sprintf(buf2,"%s",InsertTagWithAText("<i>  </i>",">",buf));
              sprintf(buf,"%s",buf2);
              break;
  
             
             }
    
       case 3: 
             {
              sprintf(buf2,"%s",InsertTagWithAText("<b>  </b>",">",buf));
              sprintf(buf,"%s",InsertTagWithAText("<i>  </i>",">",buf2));
              break;
             
             
             }
    
      default:
        break;
    
    
     }
    
  // font options :
  // font Strickthrough=fb->GetFontStrickTrhough()
  // font underline = fb->GetFontUnderline()
  
      if(fb->box_Example->GetFontStrickTrhough()!=0)
        {
         sprintf(buf2,"%s",InsertTagWithAText("<s>  </s>",">",buf));
         sprintf(buf,"%s",buf2);
         ;
         }
      if (fb->box_Example->GetFontUnderline()!=0)
        {
         sprintf(buf2,"%s",InsertTagWithAText("<u>  </u>",">",buf));                                                   
         sprintf(buf,"%s",buf2);
         }
  
  cb_InsertTag(fb,buf);
  fb->hide();
}

void cb_TabNext() {
  //This should do the next function for a tab 
    int			num_kids;
    Fl_Widget	* const *kids;
  
    if ((num_kids = txt_Tab_Edit->children()) == 0)
      return;
  
    for (kids = txt_Tab_Edit->array(); num_kids > 0; kids ++, num_kids --)
    {
      if ((*kids)->visible())
        break;
    }
  
    if (num_kids > 1)
      txt_Tab_Edit->value(kids[1]);
}

void cb_TabPrev() {
  //This should do the previous function for a tab 
   int			num_kids;
    Fl_Widget	* const *kids;
  
  
    if ((num_kids = txt_Tab_Edit->children()) == 0)
      return;
  
    for (kids = txt_Tab_Edit->array(); num_kids > 0; kids ++, num_kids --)
      if ((*kids)->visible())
        break;
  
    if (num_kids > 0 && num_kids < txt_Tab_Edit->children())
      txt_Tab_Edit->value(kids[-1]);
}
